        // 页面菜单初始化
        var wHref = window.location.href;
        $("#nav_ul").children("li").each(function () {
            if (wHref.indexOf($(this).attr("cur")) > 0) {
                $("#nav_ul").children("li").removeClass("active");
                $(this).addClass("active");
